package main

import (
	"context"
	"encoding/hex"
	"fmt"

	appPack "192.168.1.188/ipc/ipc-bridge/app"

	"github.com/ethereum/go-ethereum/accounts/abi/bind"
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/ethclient"
)

func main() {

	ethRpcAddr := fmt.Sprintf("http://192.168.1.188:810%d", 1)
	app := appPack.App{}
	//app.logger = logger

	if err := app.NewEthRpcClientAndForeignBridge(ethRpcAddr, common.HexToAddress("0x8b1E7AF8d241ED1538bfE05012Fd18909263ee7e")); err != nil {
		fmt.Println(err)
		return
	}

	app.EthAuthor = appPack.SetPrvKey("69DD6EE9FD337E70A1AE40857FB7B7614200EB2EDC10908501CF70B09B1403D2")

	//
	oldTxHashAsBytes, err := hex.DecodeString("c8338f6e4ab1ee1699b48f45745f5b351cdd70ad247becc3ba5d8633e55ee039")

	if err != nil && len(oldTxHashAsBytes) != 32 {
		fmt.Printf("err:%v  or len(txhashbyte) != 32 real len: %d \n", err, len(oldTxHashAsBytes))
		return
	}

	oldTxHashAsArray := [32]byte{}

	for i := 0; i < 32; i++ {
		oldTxHashAsArray[i] = oldTxHashAsBytes[i]
	}

	for i := 0; i < 20; i++ {

		msgStr := fmt.Sprintf("")

		app.EthAuthor.GasLimit = 10000000000
		//app.EthAuthor.GasPrice = big.NewInt(1000000)

		//sbtMsgTxHash, err := app.EthForeignBridge.SubmitMessage(app.EthAuthor, oldTxHashAsArray, []byte(msgStr))
		sbtMsgTxHash, err := app.EthForeignBridge.TestSubmitBytesMax(app.EthAuthor, []byte(msgStr))

		if err != nil {
			fmt.Println(err)
			return
		}

		ctx := context.Background()
		con, err := ethclient.Dial(ethRpcAddr)
		if err != nil {
			fmt.Println(err)
			return
		}
		receipt, err := bind.WaitMined(ctx, con, sbtMsgTxHash)
		if err != nil {
			fmt.Println(err)
			return
		}

		fmt.Printf("\n\nidx: %d tx hash:%v sbtMsgTxGas: %v  sbtMsgTxGasPrice: %v\n receipt tx hash:%v status:%v gasUsed:%v \n", i,
			sbtMsgTxHash.Hash().String(), sbtMsgTxHash.Gas(), sbtMsgTxHash.GasPrice(), receipt.TxHash.String(), receipt.Status, receipt.GasUsed)
	}

}
